    if (typeof name !== 'string') {
      throw new TypeError('name must be a string');
    }
    this._name = name;
  }

  // Getter for length
  getLength() {
    return this._length;
  }

  // Setter for length
  setLength(length) {
    if (typeof length !== 'number') {
      throw new TypeError('length must be a number');
    }
    this._length = length;
  }

  // Getter for students
  getStudents() {
    return this._students;
  }

  // Setter for students
